import styled from "styled-components";
import { useMediaQuery } from 'react-responsive';
import LogoImage from '../../assets/essencial_logo_1.png';

export const Footer = styled.div`
  background: radial-gradient(rgba(22, 74, 115, 1), rgba(22, 74, 115, .9)); // Azul Royal #164A73
  border-top: 6px solid #a8061d;
  color: #ccc;


  .inner-wrapper {
    align-items: center;
    display: flex;
    font-family: 'Roboto Condensed', sans-serif;
    font-size: 20px;
    font-weight: 400;
    justify-content: space-between;
    margin: auto;
    padding: 45px;

    @media(max-width: 768) {
      flex-direction: column;
      align-items: flex-start;
      padding: 40px;
    }

    .content {
      display: flex;
      flex-direction: column;
      font-family: 'Roboto Condensed', sans-serif;
      font-weight: 400;

      &__header {
        border-bottom: 1px solid #eee;
        font-family: 'Prosto One', cursive, sans-serif;
        font-size: 24px;
        font-weight: 600;
        margin-bottom: 15px;
        text-align: center;
        padding: 10px;

      }

      &__whatsapp {
        font-size: 14px;
        padding: 15px 0;
      }

      &__fone {
        font-size: 14px;
        padding: 15px 0;
      }

      &__endereco {
        font-size: 14px;
        padding: 15px 0;
      }


    }


    nav {
      display: flex;
      flex-direction: column;

      @media(max-width: 768) {
        padding: 40px 0;
      }

      a {
        color: #fff;
        text-decoration: none;
        font-size: '14px';
        line-height: 17px;
        margin-bottom: 16px;
        min-width: 170px;

        @media(max-width: 768) {
          margin-bottom: 7px;
        }

        &:last-child {
          margin-bottom: 0;
        }

        &:hover {
          border-bottom: 1px solid #eee;
        }
      }

    }
  }

  .social-media {
    &__icon {
      align-items: center;
      background-color: transparent;

      // background-color: rgba(255, 255, 255, 0.9);
      background-position: center;
      background-repeat: no-repeat;
      border-radius: 3%;
      color: #ccc;
      display: inline-flex; 
      height: 30px;
      margin-left: 0px;
      justify-content: center;
      overflow: hidden;
      width: 100%;
      transition: background-color .15s ease-in-out;
      text-indent: -1000px;

      @media(min-width: 768) {
        margin-left: initial;
      }

      &:hover {
        // background-color: rgba(255, 2555, 255, 0.3);
        border-bottom: 1px solid #ccc;
      }

      &--whatsapp {
        background-image: url(/images/icons/logo-whatsapp.svg);
        background-position: 3px 3px;
        background-size: 9%;
      }

      &--fone {
        background-image: url(/images/icons/phone.png);
        background-position: 3px 3px;
        background-size: 9%;
      }

    }
  }
`;

export const Logo = styled((props) => {
  const isMobile = useMediaQuery({ maxWidth: 768});
  const logo = !isMobile ? LogoImage : LogoImageShort;
  return <img src={logo} {...props} alt='Essencial - Construtora e Incorporadora' />
})`
  height: 70%;
  width: 70%;
  margin-bottom: 45px;

  @media(max-width: 768px) {
    margin-bottom: 6px;
  }
`;
