import styled from "styled-components";
import Logo from '../../assets/essencial_logo_1.png';

export const Footer = styled.div`
  background: radial-gradient(rgba(22, 74, 115, 1), rgba(22, 74, 115, .9)); // Azul Royal #164A73
  border-top: 6px solid #a8061d;
  color: #ccc;


  .inner-wrapper {
    display: flex;
    font-family: 'Roboto Condensed', sans-serif;
    font-size: 15px;
    font-weight: 400;
    justify-content: space-between;
    margin: auto;
    padding: 15px 60px;

    @media(max-width: 768) {
      flex-direction: column;
      align-items: flex-start;
      padding: 40px;
    }

    .content {
      display: flex;
      flex-direction: column;

      &__header {
        border-bottom: 1px solid #eee;
        font-family: 'Prosto One', cursive, sans-serif;
        font-size: 21px;
        font-weight: 600;
        margin-bottom: 15px;
        text-align: center;
        padding: 10px;

      }

      &__whatsapp {
        font-family: 'Roboto Condensed', sans-serif;
        font-size: 14px;
        padding: 12px 0;
      }

      &__fone {
        font-family: 'Roboto Condensed', sans-serif;
        font-size: 18px;
        padding: 12px 0;
      }

      &__endereco {
        font-family: 'Roboto Condensed', sans-serif;
        font-size: 18px;
        padding: 12px 0;
      }


    }


    nav {
      display: flex;
      flex-direction: column;

      @media(max-width: 768) {
        padding: 40px 0;
      }

      a {
        color: #fff;
        text-decoration: none;
        font-size: '14px';
        line-height: 17px;
        margin-bottom: 16px;
        min-width: 170px;

        @media(max-width: 768) {
          margin-bottom: 7px;
        }

        &:last-child {
          margin-bottom: 0;
        }

        &:hover {
          border-bottom: 1px solid #eee;
        }
      }

    }
  }
`;


export const LogoImg = styled((props) => {

  const logo = Logo;
  return <img src={logo} {...props} alt='Essencial - Construtora e Incorporadora' />
})`
  height: 150px;
  width: 234px;
  margin-bottom: 40px;

  @media(max-width: 768px) {
    margin-bottom: 3px;
  }
`;
