import styled from "styled-components";

export const MaisonEsperance = styled.div`
  text-align: center;
  width: 100vw;

  img{
    /* border: thick double; */
    border: .18rem solid;
    border-radius: 3px;
  }

  .hero {
    align-items: center;
    border-bottom: 1px solid #999;
    display: flex;
    justify-content: space-around;
    margin: 30px 30px;
    padding: 45px;

    img {
      border: none;
    }
  } 

  .inner-wrapper {
    align-items: center;
    color: #111;
    display: flex;
    justify-content: space-around;
    margin: 21px;
    width: 100%;
    padding: 45px 30px;

    .hero{
      border-bottom: 1px solid #999;
    }

    p {
      font-size: 14px;
      padding-bottom: 30px;
    }

    @media (max-width: 768px) {
      flex-direction: column ;
    }
  }

  .navbarLink {
    color: #111;
    padding: 3px 9px;
  }

  .pic {
    text-align: center;
    width: 50vw;
  }

  .text {
    padding: 0 45px;
    text-align: justify;
    width: 50vw;
  }

  @media (max-width: 768px) {
    flex-direction: column ;
  }

  .titulo {
    font-size: 1.5rem;
    text-shadow: 1px 3px 3px #ccc;
    padding: 15px 30px;
  }

  .subTitulo {
    font-size: 1.2rem;
    padding: 15px 30px;
    text-align: center;
  }

  .descricao {
    align-items: center;
    font-size: 1rem;
    line-height: 2.1;
    padding: 15px 30px;

    li {
      list-style: square;
      line-height: 3;
    }
  }
`;
