import styled from "styled-components";

export const Empreendimentos = styled.div`
  left: 0;
  margin: 0;
  right: 0;
  padding: 0;
  width: 100vw;
  z-index: -1;

  @media (max-width: 768px) {
    height: auto;
  }

  .header {
    align-items: center;
    color: rgba(22, 74, 115, 1); 
    font-family: 'Cinzel';
    font-size: 36px;
    font-weight: 600;
    margin-top: 30px;
    padding: 18px 0;
    padding-left: 10%;
    position: relative;
    text-shadow: 1px 3px 3px #ccc;
    width: 100vw;

    @media (max-width: 768px) {
      margin-top: 15px;
      padding-left: 5%;
    }
  }

  .inner-wrapper {
    align-items: center;
    color: #111;
    display: flex;
    justify-content: space-around;
    margin: 0px;
    padding: 30px;

    p {
      font-size: 14px;
    }

    @media (max-width: 768px) {
      flex-direction: column;
    }
  }
`;
